                                <h5 className="d-xl-flex align-items-xl-center col-12 col-lg-2 text-left" style={{margin: '0px'}}>
                                    <b>Email:</b>
                                </h5>

                                <p className="text-left d-xl-flex align-items-xl-center col-12 col-lg-8" style={{margin: '0px auto',marginLeft: '0px'}}>
