                                <i id="iconUp4" className="fa fa-chevron-up" style={{marginLeft: 'auto', display: 'none'}}></i>
                            </button>

                            <form id="phoneEdit" className="border rounded col-12 mt-4 mb-4" style={{display: 'none'}}>
