import React, {Component} from 'react'
import {BookCard} from '../bookCardComponent/bookCard'

const style1 = {
    backgroundColor: 'white',
    color: 'black'
}

const style2 = {
    backgroundColor: 'rgba(20, 41, 215, 0.68)',
    color: 'white'
}

const style3 = {
    backgroundColor: 'rgba(20, 41, 215, 0.85)',
    color: 'white'
}

export class BookContainer extends Component {
    constructor(props) {
        super(props)
    }

    render() {
        const tag = (this.props.tag == 'none')
        return (
            <div className="container-fluid" style={{backgroundColor: '#1429D7',margin: '25px 0px'}}>
                <div className="container row" style={{backgroundColor: 'transparent',margin: '0px auto'}}>
                    <div className="col-12 col-md-2 mt-4" style={{borderTop: '3px solid white',color: 'white'}}>
                        <label className="col-12">
                            <b>{this.props.title}</b>
                        </label>

                        {tag?
                           <noscript></noscript> 
                        : <button className="btn btn-primary border-0" style={{backgroundColor: '#F15922',height: '40px'}}>
                            <b>{this.props.tag}</b>
                        </button>}
                    </div>

                    <BookCard serial='0' title='Kỷ nguyên Trí tuệ Nhân tạo'/>
                    <BookCard serial='1' title='Chạy đua với Robot'/>
                    <BookCard serial='2' title='Năm 2062 Thời đại của trí thông minh nhân tạo'/>
                    <BookCard serial='3' title='The A.I. Age'/>
                </div>

                <button className="btn btn-primary border-0 rounded-pill mt-4 mb-4" style={{backgroundColor: '#F15922',height: '40px',width: '150px',margin: '0px auto'}}>
                    <b>...See More</b>
                </button>
            </div>

        )
    }
}
