import React, {Component} from 'react'

export class UserComment extends Component {
    constructor(props) {
        super(props)
    }

    render() {
        return (
            <div className="container" style={{padding: '20px 60px', backgroundColor: 'white'}}>
                <div className="row mt-3 mb-4">
                    <div className="col-6 col-sm-2" style={{padding: '0px', borderTop: '2px solid #1429D7'}}>
                        <div>
                            <i className="fa fa-user-circle-o mt-4" style={{color: '#1429D7', fontSize: '80px'}}></i>
                        </div>
                        <p className="text-center mt-2" style={{color: '#1429D7'}}><b>{this.props.name}</b></p>
                    </div>
                </div>
            </div>
        )
    }
}
