import React, {Component} from 'react'
import {BookCard} from '../bookCardComponent/bookCard'
import {Link} from 'react-router-dom'

const style1 = {
    backgroundColor: 'white',
    color: '#1429D7',
    margin: '25px 0px'
}

const style2 = {
    backgroundColor: 'rgba(20, 41, 215, 0.68)',
    color: 'white',
    margin: '25px 0px'
}

const style3 = {
    backgroundColor: 'rgba(20, 41, 215, 0.85)',
    color: 'white',
    margin: '25px 0px'
}

export class BookContainer extends Component {
    constructor(props) {
        super(props)
        this.state = {
            isLoading: true,
            books: [],
            defaultBooks: [
                {
                    sku: 'default',
                    serial: 0,
                    title: 'Kỷ nguyên Trí tuệ Nhân tạo'
                },
                {
                    sku: 'default',
                    serial: 1,
                    title: 'Chạy đua với Robot'
                },
                {
                    sku: 'default',
                    serial: 2,
                    title: 'Năm 2062 Thời đại của trí thông minh nhân tạo'
                },
                {
                    sku: 'default',
                    serial: 3,
                    title: 'The A.I. Age'
                }
            ]
        }
    }

    createMiniBooks(fontColor) {
        let miniBooks = []

        for (let i = 0; i < 4; i++) {
            miniBooks.push(<BookCard sku={this.state.books[i].sku} serial={this.state.books[i].serial} title={this.state.books[i].title} fontColor={fontColor}/>)
        }

        return miniBooks
    }

    createBooks(fontColor) {
        let realBooks = []

        for (let i = 0; i < this.state.books.length; i++) {
            realBooks.push(<BookCard sku={this.state.books[i].sku} serial={this.state.books[i].serial} title={this.state.books[i].title} fontColor={fontColor}/>)
        }

        return realBooks
    }

    async componentDidMount() {
        try {
            const url = 'http://localhost:2000/api/book/' + this.props.kind
            const response = await fetch(url)
            if (response.ok) {
                const data = await response.json()
                this.setState({
                    isLoading: false,
                    books: data
                })
            }
            else {
                console.log('Request failed! - Books')
            }
        }
        catch(e) {
            console.log(e)
        }
    }

    render() {
        const tag = (this.props.tag == 'none')
        let lineStyle, fontColor, borderTopColor
        if (this.props.lineStyle == '1') {
            lineStyle = style1
            borderTopColor = {borderTop: '3px solid #1429D7'}
            fontColor = {color: 'black'}
        }
        else if (this.props.lineStyle == '2') {
            lineStyle = style2
            borderTopColor = {borderTop: '3px solid white'}
            fontColor = {color: 'white'}
        }
        else {
            lineStyle = style3
            borderTopColor = {borderTop: '3px solid white'}
            fontColor = {color: 'white'}
        }

        let type = this.props.type == 'mini'
        if (type) {
            return (
                <div className="container-fluid" style={lineStyle}>

                    <div className="container row" style={{backgroundColor: 'transparent',margin: '0px auto'}}>

                        <div className="col-12 col-lg-2 mt-4" style={borderTopColor}>

                            <label className="col-12" style={{padding: '0px'}}>
                                <b>{this.props.title}</b>
                            </label>

                            {tag?
                               <noscript></noscript> 
                            : <button className="btn btn-primary border-0 col-4 col-lg-12" style={{backgroundColor: '#F15922'}}>
                                <b>{this.props.tag}</b>
                            </button>}

                        </div>

                        <BookCard sku={this.state.defaultBooks[0].sku} serial={this.state.defaultBooks[0].serial} title={this.state.defaultBooks[0].title} fontColor={fontColor} />

                        <BookCard sku={this.state.defaultBooks[1].sku} serial={this.state.defaultBooks[1].serial} title={this.state.defaultBooks[1].title} fontColor={fontColor} />

                        <BookCard sku={this.state.defaultBooks[2].sku} serial={this.state.defaultBooks[2].serial} title={this.state.defaultBooks[2].title} fontColor={fontColor} />

                        <BookCard sku={this.state.defaultBooks[3].sku} serial={this.state.defaultBooks[3].serial} title={this.state.defaultBooks[3].title} fontColor={fontColor} />

                    </div>

                    <Link to={`contents/books/${this.props.kind}`}>
                        <button className="btn btn-primary border-0 rounded-pill mt-4 mb-4" style={{backgroundColor: '#F15922',height: '40px',width: '150px',margin: '0px auto'}}>
                            <b>...See More</b>
                        </button>
                    </Link>


                </div>

            )
        }
        else {

        }

    }
}
