import React, {Component} from 'react'
import {SearchItem} from '../searchItemsComponent/searchItems'
import {Link} from 'react-router-dom'

export class Search extends Component {
    constructor(props) {
        super(props)
        this.state = {
            isLoading: true,
            books: [],
            defaultSearch: [
                {
                    serial: 0,
                    title: "Kỷ nguyên Trí tuệ Nhân tạo",
                    author: "Nhiều tác giả",
                    rates: "10",
                    availability: "20",
                    summary: "Lorem ipsum, dolor sit amet consectetur adipisicing..."
                },
                {
                    serial: 1,
                    title: "Chạy đua với Robot",
                    author: "Joseph E. Aoun",
                    rates: "10",
                    availability: "20",
                    summary: "Lorem ipsum, dolor sit amet consectetur adipisicing..."
                },
                {
                    serial: 2,
                    title: "Năm 2062 Thời đại của trí thông minh nhân tạo",
                    author: "Toby Walsh",
                    rates: "10",
                    availability: "20",
                    summary: "Lorem ipsum, dolor sit amet consectetur adipisicing..."
                },
                {
                    serial: 3,
                    title: "The A.I. Age",
                    author: "Adam Riccoboni",
                    rates: "10",
                    availability: "20",
                    summary: "Lorem ipsum, dolor sit amet consectetur adipisicing..."
                }
            ]
        }
    }

    async componentDidMount() {
        if (this.props.searchText != "") {
            try {
                const url = 'http://localhost:2000/api/book/search?key=' + this.props.searchText
                const response = await fetch(url)
                if (response.ok) {
                    const data = await response.json()
                    this.setState({
                        isLoading: false,
                        books: data
                    })
                }
                else {
                    console.log("Request failed! - Search")
                }
            }
            catch(e) {
                console.log(e)
            }

        }

    }

    createSearch() {
        let search = []

        for (let i = 0; i < this.state.books.length; i++) {
            search.push(<SearchItem sku={this.state.books[i].sku} serial={this.state.books[i].serial} title={this.state.books[i].title} author={this.state.books[i].author} rates={this.state.books[i].rates} availability={this.state.books[i].availability} summary={this.state.books[i].summary} />)
        }

        return search;
    }

    render() {
        if (this.state.isLoading) {
            return (
                <div>

                    <div className="container mt-4 mb-4">

                        <div className="row ml-3">

                            <label className=" col-4 col-lg-3 col-xl-2 text-md-left">
                                You search for:
                            </label>

                            <h5 className="col-8 col-lg-9 col-xl-10" style={{textAlign: 'left'}}>
                                <b>{this.props.searchText}</b>
                            </h5>

                            <div className="col-4 col-lg-3 col-xl-2 text-md-left" style={{padding: '5px 15px'}}>
                                <label style={{marginTop: 'auto', marginBottom: 'auto', borderBottom: '2px solid #1429D7'}}>
                                    Search by category:
                                </label>
                            </div>

                            <button className="btn btn-primary border-0 rounded-0" style={{margin: '0px 15px',backgroundColor: '#F15922'}}>
                                <b>AI</b>
                            </button>

                        </div>   

                    </div>

                    <SearchItem serial={this.state.defaultSearch[0].serial} title={this.state.defaultSearch[0].title} author={this.state.defaultSearch[0].author} rates={this.state.defaultSearch[0].rates} availability={this.state.defaultSearch[0].availability} summary={this.state.defaultSearch[0].summary}/>
                    <SearchItem serial={this.state.defaultSearch[1].serial} title={this.state.defaultSearch[1].title} author={this.state.defaultSearch[1].author} rates={this.state.defaultSearch[1].rates} availability={this.state.defaultSearch[1].availability} summary={this.state.defaultSearch[1].summary}/>
                    <SearchItem serial={this.state.defaultSearch[2].serial} title={this.state.defaultSearch[2].title} author={this.state.defaultSearch[2].author} rates={this.state.defaultSearch[2].rates} availability={this.state.defaultSearch[2].availability} summary={this.state.defaultSearch[2].summary}/>
                    <SearchItem serial={this.state.defaultSearch[3].serial} title={this.state.defaultSearch[3].title} author={this.state.defaultSearch[3].author} rates={this.state.defaultSearch[3].rates} availability={this.state.defaultSearch[3].availability} summary={this.state.defaultSearch[3].summary}/>

                    <div className="mt-4 mb-4 rounded" style={{color: '#1429D7'}}>
                        <b>No more contents</b>
                    </div>

                </div>

            )
        }
        else {
            return (
                <div>

                    <div className="container mt-4 mb-4">

                        <div className="row ml-3">

                            <label className=" col-4 col-lg-3 col-xl-2 text-md-left">
                                You search for:
                            </label>

                            <h5 className="col-8 col-lg-9 col-xl-10" style={{textAlign: 'left'}}>
                                <b>{this.props.searchText}</b>
                            </h5>

                            <div className="col-4 col-lg-3 col-xl-2 text-md-left" style={{padding: '5px 15px'}}>
                                <label style={{marginTop: 'auto', marginBottom: 'auto', borderBottom: '2px solid #1429D7'}}>
                                    Search by category:
                                </label>
                            </div>

                            <button className="btn btn-primary border-0 rounded-0" style={{margin: '0px 15px',backgroundColor: '#F15922'}}>
                                <b>AI</b>
                            </button>

                        </div>   

                    </div>

                    {this.createSearch()}

                    <div className="mt-4 mb-4 rounded" style={{color: '#1429D7'}}>
                        <b>No more contents</b>
                    </div>

                </div>

            )
        }

    }
}
